import React, { useEffect, useState } from "react";


const GoToTop = () => {
 const goToBtn = () => {
    window.scrollTo({ top: 0, left: 0, behavior: "smooth" });
  };

  return (
    <>
     
        <div className="" onClick={goToBtn}>
            <img src="assest\images\upArrow.gif" alt="" style={"max-width:1 + em"}/>
        </div>
    </>
  );
};



export default GoToTop;
