import './App.css';
import { BrowserRouter as Router, Switch, Route, Link, useLocation } from "react-router-dom";
import { useState, useEffect } from 'react';

import Footer from './components/Footer';
import Header from './components/Header';
import HeaderPrivacy from './components/HeaderPrivacy';
import Home from './Pages/Home';
import Eco from './Pages/Eco';
import Road from './Pages/Road';
import VerticalMode from "./components/slider";
import FaqPage from './Pages/FaqPage';
import TeamPage from './TeamPage';
import Partner from './Pages/Partner';
import Privacy from './Pages/Privacy';
import GoToTop from './components/GoToTop';

function App() {
  const location = useLocation();
  const [showHeaderPrivacy, setShowHeaderPrivacy] = useState(false);

  useEffect(() => {
    setShowHeaderPrivacy(location.pathname === "/privacy-policy");
  }, [location]);

  return (
    <>
      {showHeaderPrivacy ? (
        <HeaderPrivacy />
      ) : (
        <Header />
      )}
      <Router>
        <Switch>
          <Route exact path="/privacy-policy" component={Privacy} />
          <Route path="/">
            <Home />
            <Eco />
            <Road />
            <TeamPage />
            <VerticalMode />
            <Partner />
            <FaqPage />
          </Route>
        </Switch>
      </Router>
      <GoToTop />
      <Footer />
    </>
  );
}

export default App;
